  return (
    <div className="wrapper" ref={wrapper}>
      <div className="content">
      <div>
         <h1 className="intro">
            With Trello, you can make your team work more efficiently and effectively.
            </h1>
            <p className="p-intro">
            With Trello boards, columns, and cards, users can easily organize projects and prioritize them.
            </p>
            <input type="email" placeholder="email" style={{
              width: '200px',
              height: '30px'
            }}/>
            <Link style={{color:'white', backgroundColor:'#61BD4F', marginLeft: '20px',
             padding: '10px', fontWeight: '600', borderRadius: '5px'}} to="">Sign Up - It's free</Link>
         </div>
            <img src={img1} className="img1"/>
      </div>
    {cnt()}
    </div>
  );
}

export default LandingPage;
