import React, { useEffect, useRef } from "react";
import { Link } from "react-router-dom";
import ImageAndText from "./Pages/ImageAndText";
import img1 from "./trello-images/hero-a.svg";

function LandingPage() {
  const wrapper = useRef(null);
  window.addEventListener("scroll", () => {
    if (wrapper.current !== null) {
      if (wrapper.current.getBoundingClientRect().y < 0) {
        document.querySelector(".App-header").classList.add("shadow");
      } else {
        document.querySelector(".App-header").classList.remove("shadow");
      }
    }
  });
  const attrAndValues = {
    className: "content",
    wrapper: "wrapper",
    headingClass: "intro",
    heading:
      "With Trello, you can make your team work more efficiently and effectively.",
    section:
      " With Trello boards, columns, and cards, users can easily organize projects and prioritize them.",
    sectionClass: "p-intro",
    source: img1,
    imgClass: "img1",
  };

function cnt() {
  Object.keys(attrAndValues).map(e => {
    <ImageAndText e ={attrAndValues[e]} /> 
  })
  return <ImageAndText/>
}
  console.log(cnt());
  return (
    <div className="wrapper" ref={wrapper}>
    {
        Object.keys(attrAndValues).map(e => {
          <ImageAndText e ={attrAndValues[e]} /> 
        })
    }
    </div>
  );
}

export default LandingPage;















































