import React, {useRef } from "react";
import ImageAndText from "./Pages/ImageAndText";
import data from "./Pages/LandingPageData";
import Header from './Header'
function LandingPage() {
  const wrapper = useRef(null);
  
  window.addEventListener("scroll", () => {
    if (wrapper.current !== null) {
      if (wrapper.current.getBoundingClientRect().y < 0) {
      //  document.querySelector(".App-header").classList.add("shadow");
      setisOnTop(isOnTop => isOnTop = true)
      } else {
        setisOnTop(isOnTop => isOnTop = false)
      }
    }
  });

  function cnt(data) {
    return data.map((e,k) => <ImageAndText key={k} data={e} />);
  }
  return (
    <>
    <Header ref={header}></Header>
    <div className="wrapper" ref={wrapper}>
      {cnt(data)}

    </div>
    </>
  );
}

export default LandingPage;
