  const wrapper = useRef(null);
  window.addEventListener("scroll", () => {
    if (wrapper.current !== null) {
      if (wrapper.current.getBoundingClientRect().y < 0) {
        document.querySelector(".App-header").classList.add("shadow");
      } else {
        document.querySelector(".App-header").classList.remove("shadow");
      }
    }
  });
  const attrAndValues = {
    className: "content",
    wrapper: "wrapper",
    headingClass: "intro",
    heading:
      "With Trello, you can make your team work more efficiently and effectively.",
    section:
      " With Trello boards, columns, and cards, users can easily organize projects and prioritize them.",
