import React, { useEffect, useRef } from "react";
import { Link } from "react-router-dom";
import ImageAndText from "./Pages/ImageAndText";
import img1 from "./trello-images/hero-a.svg";
import img2 from "./trello-images/img2.webp";
import {  } from "./Pages";

function LandingPage() {
  const wrapper = useRef(null);
  window.addEventListener("scroll", () => {
    if (wrapper.current !== null) {
      if (wrapper.current.getBoundingClientRect().y < 0) {
        document.querySelector(".App-header").classList.add("shadow");
      } else {
        document.querySelector(".App-header").classList.remove("shadow");
      }
    }
  });

function cnt(data) {
  return <ImageAndText data={data}/>
}
  return (
    <div className="wrapper" ref={wrapper}>
   {cnt(attrAndValues)}
   {cnt(attrAndValues2)}
    </div>
  );
}

export default LandingPage;















































